    body:after {
      width: 100%;
      height: 100%;
      position: absolute;
      top: 0;
      z-index: 1000;
      content: " ";
      display: block;
      background: #000;
      opacity: 0;
      -webkit-transition: all 0.3s ease;
      transition: all 0.3s ease;
      visibility: hidden;
    }

    body.slide-left {
      -webkit-transform: translate(-130px, 0);
      -ms-transform: translate(-130px, 0);
      transform: translate(-130px, 0);
      margin-right: -130px;
      padding-right: 130px;
      height: 100%;
      overflow: hidden;
